#include <iostream>
#include <vector>
#include <cmath>
#include <limits>

using namespace std;

// 소수 판별 함수
bool isPrime(int n) {
    if (n <= 1) return false;
    if (n <= 3) return true;
    if (n % 2 == 0 || n % 3 == 0) return false;
    for (int i = 5; i * i <= n; i += 6) {
        if (n % i == 0 || n % (i + 2) == 0)
            return false;
    }
    return true;
}

// 숫자의 자릿수의 곱을 계산하는 함수
int digitProduct(int n) {
    int product = 1;
    while (n > 0) {
        product *= n % 10;
        n /= 10;
    }
    return product;
}

// 가장 작은 소수를 찾고, 해당 소수의 자릿수 곱을 반환하는 함수
int compute(vector<int> lst) {
    int minPrime = std::numeric_limits<int>::max();

    for (int num : lst) {
        if (isPrime(num) && num < minPrime) {
            minPrime = num;
        }
    }

    if (minPrime == std::numeric_limits<int>::max()) {
        // 소수가 없는 경우에는 처리할 필요가 있습니다.
        // 문제 정의에 따라 적절히 결정해야 하지만 여기서는 예외적으로 -1을 반환합니다.
        return -1;
    }

    return digitProduct(minPrime);
}

