    vector<int> lst4 = {0, 724, 32, 71, 99, 32, 6, 0, 5, 91, 83, 0, 5, 6};
    vector<int> lst5 = {0, 81, 12, 3, 1, 21};
    vector<int> lst6 = {0, 8, 1, 2, 1, 7};

