def majority_element(nums):
    if len(nums) == 1:
        return nums[0]

    candidate = nums[0]
    count = 1

    for num in nums[1:]:
        if count == 0:
            candidate = num
            count = 1
        elif num == candidate:
            count += 1
        else:
            count -= 1

    return candidate

def is_majority(nums, candidate):
    count = sum(1 for num in nums if num == candidate)
    return count > len(nums) / 2

def find_majority_element(nums):
    candidate = majority_element(nums)
    if is_majority(nums, candidate):
        return candidate
    else:
        return "No majority element found"

# Test the function
nums = [2,2,1,1,1,2,2]
print(find_majority_element(nums))  # Output: 2
```

